#!/bin/bash
# Script to compute the length motif generated by meme
# USAGE:
# memele,.sh
for FAM in `ls results/WGD2ANC*/*fasta | grep "WGD2ANC[0-9]\{5\}\.fasta" | cut -d. -f1`;
do
    LENGTH=`grep "width =" $FAM.meme.motifs | awk '{print $5}'`
    for L in $LENGTH;
    do
        NAME=$(echo $FAM | awk -F'/' '{print $3}')
        echo -e "$NAME\t$L"
    done
done
